#include <stdlib.h>
#include <assert.h>
#include <stdio.h>
#include "minimap.h"

void map(const char* target, int target_len, const char* query, int query_len, map_results_t* res)
{
    res->ovlp = false;
    //const char* seq = "";
    //int len = 11790;
    const char* name = "t";
	mm_idxopt_t iopt;
	mm_mapopt_t mopt;
	int n_threads = 1;
    //const char* query = "";
    //int query_len = 11655;
    //const char* name2 = "q";

	mm_verbose = 0; // disable message output to stderr
	mm_set_opt(0, &iopt, &mopt);
    const char* preset = "map-pb";
	mm_set_opt(preset, &iopt, &mopt);

	// open index reader
	mm_idx_reader_t *r = mm_idx_reader_open_str(&iopt);
	// traverse each part of the index
	mm_idx_t *mi = mm_idx_reader_read_str(target, target_len, name, r, n_threads);
	mm_mapopt_update(&mopt, mi); // this sets the maximum minimizer occurrence; TODO: set a better default in mm_mapopt_init()!
	mm_tbuf_t *tbuf = mm_tbuf_init(); // thread buffer; for multi-threading, allocate one tbuf for each thread
    //mm_idx_stat(mi);

	mm_reg1_t *reg;
	int j, n_reg;
	reg = mm_map(mi, query_len, query, &n_reg, tbuf, &mopt, 0); // get all hits for the query
	//reg = mm_map(mi, target_len, target, &n_reg, tbuf, &mopt, 0); // get all hits for the query
    //printf("regs %d\n", n_reg);
	for (j = 0; j < n_reg; ++j) { // traverse hits and print them out
		mm_reg1_t *r = &reg[j];
		//printf("%s\t%d\t%d\t%d\t%c\t", name2, query_len, r->qs, r->qe, "+-"[r->rev]);
		//printf("%s\t%d\t%d\t%d\t%d\t%d\t%d", mi->seq[r->rid].name, mi->seq[r->rid].len, r->rs, r->re, r->mlen, r->blen, r->mapq);
		//putchar('\n');
		free(r->p);
        res->qs = r->qs;
        res->qe = r->qe;
        res->ts = r->rs;
        res->te = r->re;
        res->strand = "+-"[r->rev];
        res->ovlp = true;
	}
	free(reg);

	mm_tbuf_destroy(tbuf);
	mm_idx_destroy(mi);
}

